import org.apache.spark.rdd.RDD // Para documentar con los tipos de los RDD

val file = sc.textFile("README.md")

val wordsPos : RDD[(String, Int)] = file.flatMap(_.split(" ").zipWithIndex) // zipWithIndex numera

val wordsPosCount : RDD[(String, (Int,Int))] = wordsPos.mapValues(pos=>(pos,1)).reduceByKey((x,y)=>(x._1 + y._1, x._2 + y._2))

val representativos = wordsPosCount.filter{case (w,(_,count))=> count > 2}

val res : RDD[(String, Double)]= representativos.mapValues{case (suma,count)=> suma / count.toDouble}

println("%table\nWord\tProm")
res.collect.foreach{case (w,v) => println("\"" + w + "\"" + "\t" + v)}

