import org.apache.spark.HashPartitioner

val links = sc.textFile("./doc/pageRank.txt")
    .map(_.split(" ")).map(ws => (ws.head,ws.tail))
    .partitionBy(new HashPartitioner(100))
    .persist()

// Inicialiso cada rank en 1.0.
// Como usamos mapValues ranks tiene la misma particion que links
var ranks = links.mapValues(v => 1.0)

// Corremos 10 iteraciones de PageRank
for (i <- 0 until 10) {
    val contributions = links.join(ranks).flatMap {
        case (pageId, (links, rank)) =>
        links.map(dest => (dest, rank / links.size))
    }
    ranks = contributions.reduceByKey((x, y) => x + y).mapValues(v => 0.15 + 0.85*v)
}

// Borramos archivo si existe
def rmDir(dir: String) : Unit = { import sys.process._ ;("rm -rf " + dir).!!}
rmDir("ranks.json")

// Guardamos el resultado
ranks.toDF().write.json("ranks.json")

links.unpersist()

